package com.conductor.app.core.model;public class Document {	private long id;	private String text;		public Document() {}	public Document(long id, String text) {		this.id = id;		this.text = text;	}	public long getId() {		return id;	}		public String getText() {		return text;	}		public void setId(long id) {		this.id = id;	}		public void setText(String text) {		this.text = text;	}}