package com.conductor.app.core.service;import java.util.Set;import com.conductor.app.core.model.Document;public interface DocumentService {	/**	 * Retrieves document by id	 * @param documentId	 * @return document object or null if not found	 */	Document get(Long documentId);	/**	 * Save new document and send it for indexing( @see {@link com.conductor.app.core.service.IndexService})	 * @param document	 * @return new document long id	 * @throws {@link com.conductor.app.core.service.IndexService} if document with this id is already in DB	 */	Long addDocument(Document document);}