package com.conductor.app.core.service.tokenization;import java.nio.charset.Charset;import java.nio.charset.CharsetEncoder;import java.util.Arrays;import java.util.Collections;import java.util.List;import org.springframework.stereotype.Component;@Componentpublic class SimpleASCIITokenizer implements WordTokenizer {	private final CharsetEncoder asciiEncoder = Charset.forName("US-ASCII").newEncoder();	@Override	public List<String> tokenize(String text) {		if (text == null || text.isEmpty()) {			return Collections.emptyList();		}		if (!isAscii(text)) {			throw new IllegalArgumentException("Text is not pure ASCII");		}		return Arrays.asList(text.split("\\W+"));	}	boolean isAscii(String v) {		return asciiEncoder.canEncode(v);	}}